import React, { Component } from "react";

import SearchPanel from "../SearchPanel";
import AppHeader from "../AppHeader";
import TodoList from "../TodoList";
import ItemStatusFilter from "../ItemStatusFliter";
import ItemAddForm from "../ItemAddForm";

import "./app.css";

export default class App extends Component {
  maxId = 100;

  state = {
    todoData: [
      this.createTodoItem("Drink Coffee"),
      this.createTodoItem("Make Awesome App"),
      this.createTodoItem("Have a luch")
      
    /*{ label: "Drink Coffee", important: false, id: 1, done:false },
      { label: "Make Awesome App", important: true, id: 2, done:false},
      { label: "Have a luch", important: false, id: 3, done:false}
      */
    ]
    
  };

  createTodoItem(label){
    return{
      label,
      important: false,
      done: false,
      id: this.maxId++
    }
  }

  deleteItem = (id) => {
    this.setState(({ todoData }) => {
     // const idx = todoData.findIndex(el => el.id === id);

     // const before = todoData.slice(0, idx);
      //const after = todoData.slice(idx + 1);

     // const newArray = [...before, ...after]; //спрэд оператор
     const newArray = todoData.filter((el) => el.id !== id)
      return {
        todoData: newArray
      };
    });
  };

  addItem = (text) => {
    const newItem = this.createTodoItem(text);
      
    this.setState(({ todoData }) => {
      const newArr = [...todoData, newItem];
      return {
        todoData: newArr
      };
    });
  };
  onToggleImportant=(id)=>{
    console.log('Toggle Important', id)


  };

  onToggleDone = (id)=> {
    this.setState(({todoData})=>{
      //update object
    const idx = todoData.findIndex(el => el.id === id);
    const oldItem = todoData[idx];
    const newItem = {...oldItem, //object spread оператор
      done: !oldItem.done};
      //consteruct new array
    console.log(newItem)

    })


  };


  render() {
    return (
      <div className="todo-app">
        <AppHeader toDo={1} done={3} />
        <div className="top-panel d-flex">
          <SearchPanel />
          <ItemStatusFilter />
        </div>
        <TodoList 
        todos={this.state.todoData} 
        onDeleted={this.deleteItem}
        onToggleImportant={this.onToggleImportant}
        onToggleDone={this.onToggleDone}
         />
        <ItemAddForm onItemAdded={this.addItem} />
      </div>
    );
  }
}
