import React, { Component } from "react";
import "./todoListItem.css";
/* в props приходят артибуты label из App.js

const TodoListItem = (props)=>{ 
     return(
        <span>{props.label}</span>
    );
};
Ниже будет анологичный код, но с деструкторизацией объекта. Значения сразу пойдут в перменную label
*/

export default class TodoListItem extends Component {


  render() {
    
    const { label, important, done, onDeleted, onToggleImportant, onToggleDone } = this.props;

    let classNames = "todo-list-item";
    if (done) {
      classNames += " done";
    }

    if (important) {
      classNames += " important";
    }

    return (
      <span className={classNames}>
        <span className="todo-list-item-label" 
         onClick={onToggleDone}>
          {label}
        </span>
        <button
          type="button"
          className="btn btn-outline-success btn-sm"
          onClick={onToggleImportant}
        >
          <i className="fa fa-exclamation" />
        </button>

        <button type="button" className="btn btn-outline-danger btn-sm"
        onClick={onDeleted}>
          <i className="fa fa-trash-o" />
        </button>
      </span>
    );
  }
}
