import React from 'react';
import TodoListItem from './TodoListItem'

const TodoList =({todos}) => { //деструкторизация props объекта в переменную todos

const elements =todos.map((item)=>{
    return(
/*
<li>
можно записать так:
    <TodoListItem
    label ={item.label}
    important = {item.important}/>
</li>

этот код можно записать короче выполнив spread оператор для объекта, это означает
взять каждое свойство из объекта item и передать в качестве атрибута вместе со значением
в todoListItem
*/
<li key={item.id}>
    <TodoListItem {...item}/>
</li>    
    );
});

return(
    <ul>
      {elements}
    </ul>
    
      );
    };
export default TodoList
